CREATE TABLE funcionario (id MEDIUMINT NOT NULL AUTO_INCREMENT, nome VARCHAR(100), rg VARCHAR(100), cpf VARCHAR(100), dataNascimento VARCHAR(100), cargo VARCHAR(100), salario double, PRIMARY KEY (id));
CREATE TABLE mercadoria (id MEDIUMINT NOT NULL AUTO_INCREMENT, nome VARCHAR(100), preco double, cor VARCHAR(100),	tamanho VARCHAR(100), produto VARCHAR(100),	PRIMARY KEY (id));
CREATE TABLE vendas (id MEDIUMINT NOT NULL AUTO_INCREMENT, subtotal double, vendedorRG VARCHAR(100), dataVenda VARCHAR(100), qtdVendas int, PRIMARY KEY (id));