CREATE TABLE funcionario (nome VARCHAR(100), rg VARCHAR(100), cpf VARCHAR(100), dataNascimento VARCHAR(100), cargo VARCHAR(100), salario double);
CREATE TABLE mercadoria (nome VARCHAR(100), preco double, cor VARCHAR(100),	tamanho VARCHAR(100), produto VARCHAR(100),	ID int);
CREATE TABLE vendas (subtotal double, vendedorRG VARCHAR(100), dataVenda VARCHAR(100), qtdVendas int, ID int);